sys_ui_policy.script_false:
function onCondition() {
	if (g_user.hasRole('itil')) {
		try{
			var incState = g_form.getValue('state');
			g_form.clearsOptions('state');
			g_form.addOption('state', 1, 'New', 0);
			g_form.addOption('state', 2, 'Active', 1);
			g_form.addOption('state', 3, 'Awaiting Problem', 2);
			g_form.addOption('state', 4, 'Awaiting User Info', 3);
			g_form.addOption('state', 5, 'Awaiting Evidence', 4);
			g_form.addOption('state', 6, 'Dismissed', 5);
			g_form.addOption('state', 7, 'Resolved', 7);
			g_form.addOption('state', 8, 'Closed', 8);
			g_form.setValue('state', state);
		}
		catch(err){
			jslog("Debug>>><your initials> - Error occured: " + err);
		}
	}
}

sys_ui_policy.script_true:
function onCondition() {

	var incState = g_form.getValue('State');
	jslog("Debug>>><your initials>: State value =  " + incState);


	if (g_user.hasRole('itil')) {
		g_form.addInfoMessage("User " + g_usr.userName + " has role 'itil'");
		g_form.removeOption('state', '3');
		g_form.removeOption('state', '4');
		g_form.removeOption('state', '5')
	}


}
